CREATE TABLE `company` (
  `id` int unsigned NOT NULL AUTO_INCREMENT,
  `profile_id` int unsigned NOT NULL COMMENT 'ID profile => profiles.id',
  `is_individual` tinyint(1) NOT NULL DEFAULT '0' COMMENT 'Признак физического лица',
  `name` varchar(255) NOT NULL COMMENT 'Имя компании',
  `price_type_id` int unsigned NOT NULL COMMENT 'Тип цен, назначенный для компании => pryce_type.id',
  `bonus` int DEFAULT NULL COMMENT 'Бонусы',
  `balance` decimal(11,2) DEFAULT NULL COMMENT 'Баланс',
  `deferral_days` int DEFAULT NULL COMMENT 'Количество дней отсрочки',
  `credit_sum` decimal(11,2) NOT NULL DEFAULT '0.00' COMMENT 'Сумма кредитного лимита',
  `receivables` decimal(11,2) NOT NULL DEFAULT '0.00' COMMENT 'Дебиторская задолженность',
  `overdue_receivables` decimal(11,2) NOT NULL DEFAULT '0.00' COMMENT 'Просроченная дебиторская задолженность',
  `overdue_duration` int NOT NULL DEFAULT '0' COMMENT 'Срок просрочки дебиторской задолженности',
  `note` text COMMENT 'Примечание для менеджера',
  `created_at` datetime DEFAULT CURRENT_TIMESTAMP,
  `updated_at` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  KEY `company_price_type_id_fk` (`price_type_id`),
  KEY `company_profile_id_fk` (`profile_id`),
  CONSTRAINT `company_price_type_id_fk` FOREIGN KEY (`price_type_id`) REFERENCES `price_type` (`id`),
  CONSTRAINT `company_profile_id_fk` FOREIGN KEY (`profile_id`) REFERENCES `profiles` (`id`)
