CREATE TABLE `order_meta` (
  `order_id` int unsigned NOT NULL,
  `user_firstname` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Имя компании',
