-- Table structure for table `products`
--

DROP TABLE IF EXISTS `products`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `products` (
  `id` int unsigned NOT NULL AUTO_INCREMENT,
  `article` varchar(60) NOT NULL COMMENT 'Артикул продукта',
  `code_manufacturer` varchar(80) DEFAULT NULL COMMENT 'Код производителя',
  `manufacturer` varchar(255) DEFAULT NULL COMMENT 'Производитель, название',
  `name_of_manufacturer` text COMMENT 'Название от производителя',
  `unit_name` varchar(30) DEFAULT NULL COMMENT 'Название единицы измерения (шт, м)',
  `gallery_id` int unsigned DEFAULT NULL COMMENT 'Cсылка на изображение товара',
  `new_image_flag` tinyint(1) NOT NULL DEFAULT '0' COMMENT 'Флаг, показывающий что изменен image_url',
  `brand_id` int unsigned DEFAULT NULL COMMENT 'Бренд (brand.id)',
  `catalog_id` int unsigned DEFAULT NULL COMMENT 'Раздел каталога (catalogs_section.id)',
  `created_at` datetime DEFAULT CURRENT_TIMESTAMP,
  `updated_at` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  KEY `products_brand_id_fk` (`brand_id`),
  KEY `products_catalog_id_fk` (`catalog_id`),
  KEY `products_gallery_id_fk` (`gallery_id`),
  KEY `products_article_idx` (`article`),
  KEY `products_code_manufacturer_idx` (`code_manufacturer`),
  CONSTRAINT `products_brand_id_fk` FOREIGN KEY (`brand_id`) REFERENCES `brands` (`id`),
  CONSTRAINT `products_catalog_id_fk` FOREIGN KEY (`catalog_id`) REFERENCES `catalogs` (`id`),
  CONSTRAINT `products_gallery_id_fk` FOREIGN KEY (`gallery_id`) REFERENCES `gallery` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=101 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='Таблица продуктов';
/*!40101 SET character_set_client = @saved_cs_client */;

